changeSet.sql:
ALTER TABLE users
    ADD (
    google_id VARCHAR2(255),
    apple_id VARCHAR2(255),
    facebook_id VARCHAR2(255)
);

ALTER TABLE users
    ADD CONSTRAINT uk_users_google_id UNIQUE (google_id);
ALTER TABLE users
    ADD CONSTRAINT uk_users_apple_id UNIQUE (apple_id);
ALTER TABLE users
    ADD CONSTRAINT uk_users_facebook_id UNIQUE (facebook_id);

COMMENT ON COLUMN users.google_id IS 'Google OAuth provider unique identifier';
COMMENT ON COLUMN users.apple_id IS 'Apple Sign-In unique identifier';
COMMENT ON COLUMN users.facebook_id IS 'Facebook Login unique identifier';

changeSet.rollback:
